using UnityEngine;using System.Collections;using UnityEngine.UI;public class HomebaseController : MonoBehaviour{	public int Hp = 3;	public GameObject HpDisplayer;	public LevelManager LvlManager;	private Text _text;	void Awake()	{		_text = HpDisplayer.GetComponent<Text>();		_text.text = Hp.ToString();	}	void OnTriggerEnter2D(Collider2D col)	{		if (--Hp == 0) LvlManager.LoadLevel("03b Lose");		Destroy(col.gameObject);		_text.text = Hp.ToString();	}}